function RunCommand {
   Param ([string]$cmdToRun)

   echo "`r`n===========================================`r`n`t`t $cmdToRun `r`n===========================================`r`n"
   try   { Invoke-Expression($cmdToRun) -ErrorAction Stop }
   catch { $error[0].ToString()}

}

